            <h1 className="ui dividing header">The Things</h1>

            <div className="post-container">
            {posts.map((item) => {
                return <PostItem 
                key={item._id}  
                posts={item} 
                setPost={setPost} 
                token={token}/>
            })}
            </div>
        </div>
    </>
    );
};

export default Posts;
