import React from "react";
import {Link} from "react-router-dom";

const PostItem = ({posts}) => {
    return (
    <div className="ui card">
        <div className="content">
            <div className="left floated aligned header">{posts.title}</div>
            {posts.isCreator ? <div className="right floated aligned tiny header">Mine</div> : null}
            <div className="centered aligned description">
                <p>{posts.description}</p>
                <div className="extra content">
                    <Link to="">View Location</Link>
                </div>
            </div>
        </div>
        <div role="list" className="ui divided relaxed list" style={{color: '#444'}}>
            {vacation.comments.map((comment) => {
                return (
                <div role="listitem" className="item">
                    <b>{comment.guest.username}</b>
                    <p className="content">{comment.content}</p>
                </div>);
            })}
        </div>
    </div>
    );
};

export default PostItem;
