import React, {useState} from "react";
import { useHistory } from "react-router-dom";
import { createPost } from "../api/api";

const PostCreateForm = ({token, setPost}) => {
    const history = useHistory();
    const [description, setDescription] = useState('');
    const [location, setLocation] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);

    return (
    <form 
        className="ui form" 
        onSubmit={async (event) => {
            event.preventDefault();

        const {error, post} = await createPost(
            token, 
            title,
            description, 
            price,
            location,
            willDeliver
        );

        if (post) {
            setPost((prevPosts) => [...prevPosts, post]);
            setDescription('');
            setLocation('');
            history.push('/posts');
        } else {
            setErrorMessage(error);
        }


    }}>
        <h2>Create Post</h2>
        <div className="field">
            <label htmlFor="title">Title</label>
            <input 
                name="title" 
                type="text" 
                placeholder="Post Title" 
                required autoComplete="off"
                value={title}
                onChange={(event) => setTitle(event.target.value)}></input>
        </div>

        <div className="field">
            <label htmlFor="description">Description</label>
            <input 
                name="description" 
                type="text" 
                placeholder="A description of Item" 
                required autoComplete="off"
                value={description}
                onChange={(event) => setDescription(event.target.value)}></input>
        </div>

        <div className="field">
            <label htmlFor="price">price</label>
            <input 
                name="price" 
                type="text" 
                placeholder="Item Price" 
                required autoComplete="off"
                value={price}
                onChange={(event) => setPrice(event.target.value)}></input>
        </div>

        <div className="field">
            <label htmlFor="location">Location</label>
            <input 
                name="location"
                type="text" 
                placeholder="Where item is located" 
                autoComplete="off"
                value={location}
                onChange={(event) => setLocation(event.target.value)}
            ></input>
        </div>

        <div className="field">
            <label htmlFor="willDeliver">willDeliver</label>
            <input 
                name="willDeliver"
                type="text" 
                placeholder="Where item is located" 
                autoComplete="off"
                value={willDeliver}
                onChange={(event) => setWillDeliver(event.target.value)}
            ></input>
        </div>



        <button type="submit" className="ui button">
            Create
        </button>
    </form>
    );
};

export default PostCreateForm;
