import React, {useState} from "react";
import { useHistory } from "react-router-dom";
import { createPost } from "../api/api";

const PostCreateForm = ({token, setPosts}) => {
    const history = useHistory();
    const [description, setDescription] = useState('');
    const [location, setLocation] = useState('');

    return (<form className="ui form" onSubmit={async (event) => {
        event.preventDefault();

        const {error, post} = createPost(token, description, location);

        if (post) {
            setPosts((prevPosts) => [...prevPosts, post]);
            setDescription('');
            setLocation('');
            // history.push('/posts');
        } else{}


    }}>
        <h2>Create Post</h2>
        <div className="field">
            <label htmlFor="description">Description</label>
            <input name="description" type="text" placeholder="A description of Item" required autoComplete="off"
                value={description}
                onChange={(event) => setDescription(event.target.value)}></input>
        </div>

        <div className="field">
            <label htmlFor="location">Location</label>
            <input type="text" placeholder="Where item is located" autoComplete="off">
                value={location}
                onChange={(event) => setLocation(event.target.value)}
            </input>
        </div>

        <button type="submit" className="ui button">Create</button>
    </form>
    );
};

export default PostCreateForm;
