const baseURL ='https://strangers-things.herokuapp.com/api/2207-FTB-ET-WEB-PT/';

const makeHeaders = (token) => {
    console.log("running makeHeaders")
    const headers = {
        "Content-Type": "application/json",
    };

    if (token) {
        headers['Authorization'] = `Bearer ${token}`;
    }

    return headers;
};

