export const fetchPosts = async(token) => {
    try{
        console.log("running fetchPosts")
        const {success, error, data} = await callAPI("posts", {
            token:token,
        });

        if (success) {
            return {
                error: null,
                posts: data.posts
            };
        } else {
            return {
                error: error.message,
                posts: []
            };
        }
    } catch (error) {
        console.error("Error in fetchPosts!!!", error);
        return {
            error: "Failed to load Posts",
            posts: []
        };
    }
};

